sysauto_script.script:
var grUtil = new global.GlideRecordUtil();
var closedReviewsGR;
var grMap = {};
var duration = gs.getProperty('x_snc_rcr_hub.RCR_SLIDING_WINDOW');
var gr = new GlideRecord('x_snc_rcr_hub_review_results');
var condition = "review_last_seen<=javascript:gs.daysAgoStart(2)^NQreview_status=ignored^sys_created_on<=javascript:gs.daysAgoStart(" + duration + ")";
gr.addEncodedQuery(condition);
gr.query();
while(gr.next()) {
	closedReviewsGR = new GlideRecord('x_snc_rcr_hub_closed_review_results');
	closedReviewsGR.initialize();
	grUtil.populateFromGR(grMap, gr);
	grUtil.mergeToGR(grMap, closedReviewsGR);
	closedReviewsGR.insert();	
}
gr.query();
gr.deleteMultiple();
